import React, {Component} from 'react';
import Select from 'react-select';
import axios from 'axios';



export default class EditDepartament extends Component {
  state={
    name:'',
    cou_id:'',
    countries:[]
  }
  handleChange = e =>{
    this.setState({name: e.target.value});

  }
  handleChange1 = e =>{
    this.setState({code: e.target.value});
  }
  handleSubmit = e =>{
    e.preventDefault();
    // const country = {
    //   name: this.state.name,
    //   code:  this.state.code
    // }
    const depart ={
      country_id: this.props.match.params.depart_id

    }
    //console.log(country.country_id);
    axios.put(`http://localhost:8000/api/departaments/${depart.depart_id}`, {name: this.state.name, cou_id: this.state.cou_id}).then(
      res=>{
        console.log(res.data);
      }
    );
  }
  componentDidMount(){
      const country ={
        country_id: this.props.match.params.country_id

      }
    console.log('llega: '+country.country_id);
    axios.get(`http://localhost:8000/api/countries/${country.country_id}`).then(res=>{
      console.log('+res.data');
      this.setState({country_id:res.data.id, name: res.data.name, code: res.data.code});
    }).catch(error=>{alert(error)});
  }
  Country() {
          return (this.state.countries.map(data => ({ label: data.name, value: data.country_id })));
  }

  render(){


    return(
      <form onSubmit={this.handleSubmit}>
        <label>
        Nombre:
        <input type='text' name='name' onChange={this.handleChange} />
        </label>
        <label>
        Pais:
        {/*<input type='text' cou_id='cou_id' onChange={this.handleChange1} />*/}
        <Select cou_id='cou_id' options={this.Country()} onChange={this.handleChange1}/>
        </label>
        <button type='submit'>Guardar</button>
      </form>
    );
  }

}
