import React,{Component} from 'react';
import axios from 'axios';

export default class EditCountry extends Component{
  state={
    country_id: 0,
    name: '',
    code: '',
  }
  handleChange = e =>{
    this.setState({name: e.target.value});

  }
  handleChange1 = e =>{
    this.setState({code: e.target.value});
  }
  // componentDidMount(){
  //
  //   axios.get(`http://localhost:8000/api/countries/${this.props.match.country_id}`).then(res=>{
  //     console.log(res);
  //     this.setState({countries: res.data});
  //   }).catch(error=>{alert(error)});
  // }


  render(){


    return(
      <form onSubmit={this.handleSubmit}>
        <label>
        Nombre:
        <input type='text' name='name' value={this.state.name} onChange={this.handleChange} />
        </label>
        <label>
        Acronimo:
        <input type='text' code='code' value={this.state.code} onChange={this.handleChange1} />
        </label>
        <button type='submit'>Guardar</button>
      </form>

    )
  }

}
